package com.javalec.function;

public class Calc {

	
	public Calc() {
		// TODO Auto-generated constructor stub
	}
	
	public int add(int i, int j) {
		return i+j;
	}
	public int subtract(int i, int j) {
		return i-j;
	}
	public int multiply(int i, int j) {
		return i*j;
	}
	public int divide(int i, int j) {
		return i/j;
	}
	
}
